// Extract AI Review and Parse it
const aiReview = $input.item.json.content?.[0]?.text || $input.item.json.output || $input.item.json;

// Parse AI review response
let reviewParsed;
try {
  if (typeof aiReview === 'string') {
    const jsonMatch = aiReview.match(/```json\n?([\s\S]*?)\n?```/) || 
                      aiReview.match(/\{[\s\S]*\}$/);

    if (jsonMatch) {
      reviewParsed = JSON.parse(jsonMatch[1] || jsonMatch[0]);
    } else {
      reviewParsed = JSON.parse(aiReview);
    }
  } else {
    reviewParsed = aiReview;
  }
} catch (e) {
  throw new Error('Failed to parse AI review: ' + e.message);
}

// Get webhook data using the $ function to reference the Review Entry node directly
const webhookNode = $('Review Entry').first();
const webhookData = webhookNode.json;
const originalData = webhookData.data;

// Clean content function
function cleanBulletPoints(content) {
  return content
    .replace(/\s*•\s*•\s*•\s*•\s*/g, '- ')           // Replace 4 bullets with dash
    .replace(/^\s*•\s*•\s*•\s*•\s*/gm, '- ')        // Handle line starts with 4 bullets
    .replace(/\s*\u2022\s*\u2022\s*\u2022\s*\u2022\s*/g, '- ') // Unicode version
    .replace(/^\s*\u2022\s*\u2022\s*\u2022\s*\u2022\s*/gm, '- ') // Unicode line starts
    .replace(/\s*•{2,}\s*/g, '- ')                  // Any multiple bullets (2 or more)
    .replace(/\s*\u2022{2,}\s*/g, '- ');           // Unicode multiple bullets
}

// Build structured output for approved entry
const approvedEntry = {
  // Core identifiers
  entry_id: originalData.entry_id || originalData.database_entry?.id,
  user_id: webhookData.user_id,

  // Keep the approved content as-is but clean up bullets
  title: originalData.title,
  summary: originalData.summary,
  content_raw: cleanBulletPoints(originalData.content),

  // Merge existing metadata with AI review
  existing_metadata: originalData.metadata,

  // New quality scores from AI review
  quality_scores: reviewParsed.quality_scores,
  changes_made: reviewParsed.changes_made,
  remaining_enhancements: reviewParsed.remaining_enhancements,
  review_summary: reviewParsed.review_summary,
  changes_detected: reviewParsed.changes_detected,

  // Set to active since user approved
  workflow_status: 'active',
  review_status: 'approved',

  // Review metadata
  reviewed_at: new Date().toISOString(),
  reviewed_by: webhookData.user_id,
  review_notes: originalData.review_notes || "User approved after editing"
};

return {
  json: {
    success: true,
    approved_entry: approvedEntry,
    ai_review: reviewParsed
  }
};

// Use the approved content directly (already in markdown format)
const entry = $json.approved_entry;

// The content is already formatted markdown from the user's edits
// Just ensure it's properly formatted
let markdown = entry.content_raw;

// If content needs any cleanup or isn't already markdown
if (!markdown.includes('##')) {
  // Fallback to generate markdown if needed
  markdown = `## Product Overview\n${entry.content_raw}`;
}

// CREATE UPDATED METADATA OBJECT
const metadata = {
  // Merge existing metadata
  ...entry.existing_metadata,

  // Update quality assessment with AI review
  quality_assessment: {
    completeness: entry.quality_scores.completeness,
    sales_readiness: entry.quality_scores.sales_readiness,
    overall_quality: entry.quality_scores.overall_quality,
    improvements_made: entry.improvements_made,
    future_enhancements: entry.remaining_enhancements,
    ai_review_summary: entry.review_summary
  },

  // Approval info
  approval_info: {
    approved_by: entry.reviewed_by,
    approved_at: entry.reviewed_at,
    review_notes: entry.review_notes
  },

  // Update source info
  source_info: {
    ...entry.existing_metadata?.source_info,
    last_ai_review: new Date().toISOString(),
    ai_generation_count: (entry.existing_metadata?.source_info?.ai_generation_count || 0) + 1
  },

  // Keep workflow metadata
  workflow_metadata: {
    ...entry.existing_metadata?.workflow_metadata,
    approved: true,
    production_ready: true
  }
};

return {
  json: {
    ...entry,
    title: entry.title,
    summary: entry.summary,
    markdown_content: markdown,
    quality_metadata: metadata,
    ready_to_save: true
  }
};

// Prepare final data for Supabase approval update
const data = $json;

return {
  json: {
    // Update knowledge_base table
    entry_id: data.entry_id,
    title: data.title,
    summary: data.summary,

    // Keep using base64 encoding
    content: Buffer.from(data.markdown_content).toString('base64'),
    metadata: Buffer.from(JSON.stringify(data.quality_metadata)).toString('base64'),

    // Approval specific fields
    workflow_status: 'active', // Active since approved
    review_status: 'approved',
    reviewed_at: data.reviewed_at,
    reviewed_by: data.reviewed_by,

    // Workflow metadata
    workflow_metadata: {
      processed_at: new Date().toISOString(),
      approval_completed: true,
      ai_review_completed: true,
      quality_scores: {
        completeness: data.quality_scores.completeness,
        sales_readiness: data.quality_scores.sales_readiness,
        overall_quality: data.quality_scores.overall_quality
      }
    }
  }
};

