const allInputs = $input.all();
const profileData = allInputs[0].json;  // First item = profile
const postsData = allInputs[1].json;    // Second item = posts

// Extract actual data (handle array wrapping)
const profile = Array.isArray(profileData) ? profileData[0] : profileData;
const posts = Array.isArray(postsData) ? postsData[0] : postsData;

// Clean posts and filter for original content only
const allCleanedPosts = (posts.items || []).map(post => ({
  date: post.date,
  text: post.text,
  engagement: {
    reactions: post.reaction_counter || 0,
    comments: post.comment_counter || 0,
    impressions: post.impressions_counter || 0
  },
  is_repost: post.is_repost || false,
  author: post.is_repost ? post.reposted_by?.name : post.author?.name
}));

// Filter to get only original posts, then take top 5
const originalPosts = allCleanedPosts
  .filter(post => !post.is_repost)
  .slice(0, 5);

// Build final research data - ENHANCED PROFILE DATA
const researchData = {
  // Basic profile info
  name: `${profile.first_name} ${profile.last_name}`,
  headline: profile.headline,  // "CEO | AI Engineer | Behavioural Scientist"
  location: profile.location,
  public_identifier: profile.public_identifier,

  // Network & social proof
  follower_count: profile.follower_count,
  connections_count: profile.connections_count,
  shared_connections_count: profile.shared_connections_count,
  network_distance: profile.network_distance,

  // Profile insights
  is_premium: profile.is_premium,
  is_creator: profile.is_creator,
  is_influencer: profile.is_influencer,

  // Business/interests  
  creator_website: profile.creator_website,
  hashtags: profile.hashtags,

  // Posts data - only original posts
  recent_posts: originalPosts,
  post_count: originalPosts.length,
  research_metadata: {
    timestamp: new Date().toISOString(),
    total_posts_fetched: posts.items?.length || 0,
    original_posts_found: originalPosts.length,
    reposts_filtered_out: allCleanedPosts.filter(post => post.is_repost).length,
    research_depth: "standard"
  }
};

// URL-encode the public_identifier to preserve emojis as %XX format
const encodedIdentifier = encodeURIComponent(profile.public_identifier || '');

// Output ready for PostgreSQL save
return [{
  json: {
    json: {
      prospect_research: {
        profile_url: `https://linkedin.com/in/${encodedIdentifier}/`,
        profile_picture_url: profile.profile_picture_url || null,
        profile_type: "personal_prospect",
        research_data: researchData,
        research_depth: "standard",
        last_researched_at: new Date().toISOString()
      }
    }
  }
}];

