const allInputs = $input.all();

// Safely extract content with fallbacks
const getContent = (index) => {
  const item = allInputs[index];
  if (item && item.json && item.json.content && item.json.content[0] && item.json.content[0].text) {
    return item.json.content[0].text;
  }
  return `Content not available for item ${index}`;
};

// Categorize content based on patterns/headers in the text
const categorizeContent = (text) => {
  if (text.includes('Product Summary') || text.includes('Product Overview')) {
    return 'product';
  } else if (text.includes('LinkedIn Relationship Intelligence') || text.includes('LinkedIn Research Summary')) {
    return 'linkedin';
  } else if (text.includes('ICP Analysis')) {
    return 'icp';
  }
  return 'other';
};

// Extract and categorize all content
const productSummaries = [];
const linkedinSummaries = [];
const icpAnalyses = [];
const otherContent = [];

for (let i = 0; i < allInputs.length; i++) {
  const content = getContent(i);
  const category = categorizeContent(content);

  switch(category) {
    case 'product':
      productSummaries.push(content);
      break;
    case 'linkedin':
      linkedinSummaries.push(content);
      break;
    case 'icp':
      icpAnalyses.push(content);
      break;
    default:
      otherContent.push(content);
  }
}

return [{
  json: {
    USERS_PRODUCT_CAPABILITIES: productSummaries.join('\n\n---\n\n'),

    LINKEDIN_RELATIONSHIP_RESEARCH: linkedinSummaries.join('\n\n---\n\n'),

    TARGET_CUSTOMER_PROFILE_ANALYSIS: icpAnalyses.join('\n\n---\n\n'),

    ADDITIONAL_CONTEXT: otherContent.length > 0 ? otherContent.join('\n\n---\n\n') : 'No additional context',

    DATA_SOURCE_WARNING: "IMPORTANT: LinkedIn activities mentioned in LINKEDIN_RELATIONSHIP_RESEARCH are for both the USER (sender) and PROSPECT'S personal activities and achievements. Make sure to check whose name is attributed to an achievement and never use the prospect's achievements as an example of the user's product success.",

    item_count: allInputs.length
  }
}];

// Parse the AI response
const textContent = $json.content[0].text;

let parsedData;

try {
  // Try parsing directly first (most common case)
  parsedData = JSON.parse(textContent);
} catch (e) {
  // If that fails, try extracting from code block
  const jsonMatch = textContent.match(/```json\n([\s\S]*?)\n```/);
  if (jsonMatch) {
    parsedData = JSON.parse(jsonMatch[1]);
  } else {
    // Try to find JSON object anywhere in the text
    const directJsonMatch = textContent.match(/\{[\s\S]*\}/);
    if (directJsonMatch) {
      parsedData = JSON.parse(directJsonMatch[0]);
    } else {
      throw new Error('No valid JSON found in AI response: ' + textContent.substring(0, 500));
    }
  }
}

// Validate the parsed data has the expected structure
if (!parsedData.message) {
  throw new Error('Parsed data missing required "message" field');
}

// Return the separated data
return {
  generated_message: parsedData.message,
  message_metadata: JSON.stringify({
    icp_analysis: parsedData.icp_analysis || null,
    message_metadata: parsedData.message_metadata || null
  })
};

// Get all items from the merge node
const items = $input.all();

// Find the item with generated_message and the item with context
const messageItem = items.find(item => item.json.generated_message);
const contextItem = items.find(item => item.json.USERS_PRODUCT_CAPABILITIES);
const startItem = items.find(item => item.json.user_id || item.json.prospect_url || item.json.icp_id);

if (!messageItem) {
  throw new Error('No generated_message found in input items');
}

// Safe parsing with null checks
let messageMetadata = null;
if (messageItem.json.message_metadata) {
  if (typeof messageItem.json.message_metadata === 'string') {
    try {
      messageMetadata = JSON.parse(messageItem.json.message_metadata);
    } catch (e) {
      console.error('Failed to parse message_metadata:', e);
      messageMetadata = null;
    }
  } else {
    messageMetadata = messageItem.json.message_metadata;
  }
}

// Build AI context separately (all the research/context data)
const aiContext = {
  product_capabilities: contextItem?.json.USERS_PRODUCT_CAPABILITIES || '',
  linkedin_research: contextItem?.json.LINKEDIN_RELATIONSHIP_RESEARCH || '',
  icp_profile: contextItem?.json.TARGET_CUSTOMER_PROFILE_ANALYSIS || '',
  additional_context: contextItem?.json.ADDITIONAL_CONTEXT || ''
};

// Build message metadata separately (ICP analysis and message metadata only)
const messageMetadataOnly = {
  icp_analysis: messageMetadata?.icp_analysis || null,
  message_metadata: messageMetadata?.message_metadata || null,
  source_info: {
    generated_at: new Date().toISOString(),
    generation_method: 'ai_personalized'
  }
};

return {
  json: {
    user_id: startItem?.json.user_id || messageItem.json.user_id,
    prospect_url: startItem?.json.prospect_url || messageItem.json.prospect_url,
    icp_id: startItem?.json.icp_id || messageItem.json.icp_id,
    generated_message: messageItem.json.generated_message,
    ai_context: Buffer.from(JSON.stringify(aiContext)).toString('base64'),
    message_metadata: Buffer.from(JSON.stringify(messageMetadataOnly)).toString('base64'),
    generated_at: new Date().toISOString()
  }
};

